  prod_n *= xi - x[i-r+0];  /* n = 0 */
  prod_n *= xi - x[i-r+2];  /* n = 2 */
  sum_m += prod_n;

  prod_m = 1.0;
  prod_m *= x[i-r+1] - x[i-r+0];
  prod_m *= x[i-r+1] - x[i-r+2];
  prod_m *= x[i-r+1] - x[i-r+3];

  sum_l += sum_m / prod_m * (x[i-r+0+1] - x[i-r+0]);

  /* l = 2 */
  sum_m = 0.0;

  /* m = 0 */
  prod_n = 1.0;
  prod_n *= xi - x[i-r+1];  /* n = 1 */
  prod_n *= xi - x[i-r+3];  /* n = 3 */
