  prod_n *= xi - x[i-r+1];  /* n = 1 */
  sum_m += prod_n;

  prod_m = 1.0;
  prod_m *= x[i-r+2] - x[i-r+0];
  prod_m *= x[i-r+2] - x[i-r+1];
  prod_m *= x[i-r+2] - x[i-r+3];

  sum_l += sum_m / prod_m * (x[i-r+0+1] - x[i-r+0]);

  /* l = 3 */
