  prod_m *= x[i-r+3] - x[i-r+1];
  prod_m *= x[i-r+3] - x[i-r+2];

  sum_l += sum_m / prod_m * (x[i-r+0+1] - x[i-r+0]);

  c[0] = sum_l;

